import React, { useState, useEffect } from 'react';
import './Dashboard.css';

const Dashboard = ({ user, onLogout }) => {
  const [attendanceData, setAttendanceData] = useState([]);
  const [stats, setStats] = useState({
    totalStudents: 0,
    todayAttendance: 0,
    weeklyAttendance: 0
  });
  const [isLoading, setIsLoading] = useState(true);
  const [activeSection, setActiveSection] = useState('overview');

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      console.log('Fetching dashboard data...');
      const response = await fetch('/api/dashboard/stats');
      console.log('Response status:', response.status);

      const data = await response.json();
      console.log('Response data:', data);

      if (response.ok && data.success) {
        setStats(data.stats);
        setAttendanceData(data.recentAttendance || []);
        console.log('Dashboard data loaded successfully');
      } else {
        console.error('API error:', data.error || 'Unknown error');
      }
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
    } finally {
      setIsLoading(false);
    }
  };

  // Export function - can be used in reports section if needed
  // const handleExportData = async () => {
  //   try {
  //     const response = await fetch('/api/dashboard/export');
  //     const blob = await response.blob();
  //     const url = window.URL.createObjectURL(blob);
  //     const a = document.createElement('a');
  //     a.href = url;
  //     a.download = `attendance_report_${new Date().toISOString().split('T')[0]}.csv`;
  //     document.body.appendChild(a);
  //     a.click();
  //     window.URL.revokeObjectURL(url);
  //     document.body.removeChild(a);
  //   } catch (error) {
  //     console.error('Error exporting data:', error);
  //   }
  // };

  if (isLoading) {
    return (
      <div className="dashboard-container">
        <div className="loading">Loading dashboard...</div>
      </div>
    );
  }

  return (
    <div className="dashboard-container">
      {/* Top Navigation Header */}
      <div className="dashboard-header">
        <div className="header-left">
          <div className="logo-section">
            <i className="fas fa-cube"></i>
            <span className="brand-name">Ketema</span>
          </div>
          <nav className="top-nav">
            <a href="#" className="nav-link">Home</a>
            <a href="#" className="nav-link">About</a>
            <a href="#" className="nav-link">Skills</a>
            <a href="#" className="nav-link">Projects</a>
            <a href="#" className="nav-link">Contact</a>
            <a href="#" className="nav-link active">Dashboard</a>
          </nav>
        </div>
      </div>

      <div className="dashboard-layout">
        {/* Left Sidebar */}
        <div className="sidebar">
          <div className="sidebar-header">
            <h2>Dashboard</h2>
            <p className="welcome-text">Welcome, admin</p>
          </div>
          <nav className="sidebar-nav">
            <div 
              className={`nav-item ${activeSection === 'overview' ? 'active' : ''}`}
              onClick={() => setActiveSection('overview')}
            >
              <i className="fas fa-chart-bar"></i>
              <span>Overview</span>
            </div>
            <div 
              className={`nav-item ${activeSection === 'attendance' ? 'active' : ''}`}
              onClick={() => setActiveSection('attendance')}
            >
              <i className="fas fa-calendar-check"></i>
              <span>Attendance</span>
            </div>
            <div 
              className={`nav-item ${activeSection === 'students' ? 'active' : ''}`}
              onClick={() => setActiveSection('students')}
            >
              <i className="fas fa-users"></i>
              <span>Students</span>
            </div>
            <div 
              className={`nav-item ${activeSection === 'reports' ? 'active' : ''}`}
              onClick={() => setActiveSection('reports')}
            >
              <i className="fas fa-chart-line"></i>
              <span>Reports</span>
            </div>
            <div 
              className={`nav-item ${activeSection === 'settings' ? 'active' : ''}`}
              onClick={() => setActiveSection('settings')}
            >
              <i className="fas fa-cog"></i>
              <span>Settings</span>
            </div>
          </nav>
        </div>

        {/* Main Content */}
        <div className="main-content">
          <div className="content-header">
            <div className="header-left-content">
              <h1>
                {activeSection === 'overview' && 'Dashboard Overview'}
                {activeSection === 'attendance' && 'Attendance'}
                {activeSection === 'students' && 'Students'}
                {activeSection === 'reports' && 'Reports'}
                {activeSection === 'settings' && 'Settings'}
              </h1>
            </div>
            <div className="header-actions">
              <span className="last-updated">Last Updated: 07:51:11 PM</span>
              <button onClick={fetchDashboardData} className="refresh-btn">
                <i className="fas fa-sync-alt"></i> Refresh
              </button>
            </div>
          </div>

          {/* Content Area */}
          <div>
            {activeSection === 'overview' && (
              <>
                {/* Stats Grid */}
                <div className="stats-grid">
                  <div className="stat-card total-students">
                    <div className="stat-content">
                      <h3>TOTAL STUDENTS</h3>
                      <div className="stat-number">{stats.totalStudents}</div>
                    </div>
                  </div>

                  <div className="stat-card today-attendance">
                    <div className="stat-content">
                      <h3>TODAY'S ATTENDANCE</h3>
                      <div className="stat-number">{stats.todayAttendance}</div>
                    </div>
                  </div>

                  <div className="stat-card weekly-attendance">
                    <div className="stat-content">
                      <h3>WEEKLY ATTENDANCE</h3>
                      <div className="stat-number">{stats.weeklyAttendance}</div>
                    </div>
                  </div>
                </div>

                {/* Dashboard Summary */}
                <div className="recent-activity">
                  <h2>Dashboard Summary</h2>
                  <div className="activity-summary">
                    <p>Welcome to the Student Attendance Management System. Here you can view overall statistics and recent activity.</p>
                    <ul>
                      <li>Monitor daily attendance rates</li>
                      <li>Track weekly attendance trends</li>
                      <li>View total registered students</li>
                      <li>Access detailed reports and analytics</li>
                    </ul>
                  </div>
                </div>
              </>
            )}

            {activeSection === 'attendance' && (
              <>
                <div className="section-description">
                  <p>View and manage student attendance records. Track meal attendance and monitor student participation.</p>
                </div>

                <div className="attendance-section">
                  <h2>Recent Attendance Records</h2>
                  <div className="attendance-table">
                    <table>
                      <thead>
                        <tr>
                          <th>Student ID</th>
                          <th>Name</th>
                          <th>Date</th>
                          <th>Time</th>
                          <th>Meal Type</th>
                        </tr>
                      </thead>
                      <tbody>
                        {attendanceData.length > 0 ? (
                          attendanceData.map((record, index) => (
                            <tr key={index}>
                              <td>{record.studentId}</td>
                              <td>{record.studentName}</td>
                              <td>{new Date(record.date).toLocaleDateString()}</td>
                              <td>{new Date(record.date).toLocaleTimeString()}</td>
                              <td>
                                <span className={`meal-type ${record.mealType?.toLowerCase()}`}>
                                  {record.mealType || 'lunch'}
                                </span>
                              </td>
                            </tr>
                          ))
                        ) : (
                          <tr>
                            <td colSpan="5" className="no-data">No recent attendance records</td>
                          </tr>
                        )}
                      </tbody>
                    </table>
                  </div>
                </div>
              </>
            )}

            {activeSection === 'students' && (
              <>
                <div className="section-description">
                  <p>Manage student information, registration, and profiles. Add new students or update existing records.</p>
                </div>

                <div className="students-section">
                  <h2>Student Management</h2>
                  <div className="feature-list">
                    <div className="feature-item">
                      <i className="fas fa-user-plus"></i>
                      <span>Add New Students</span>
                    </div>
                    <div className="feature-item">
                      <i className="fas fa-edit"></i>
                      <span>Edit Student Information</span>
                    </div>
                    <div className="feature-item">
                      <i className="fas fa-search"></i>
                      <span>Search Students</span>
                    </div>
                  </div>
                </div>
              </>
            )}

            {activeSection === 'reports' && (
              <>
                <div className="section-description">
                  <p>Generate detailed attendance reports and view analytics. Export data for further analysis.</p>
                </div>

                <div className="reports-section">
                  <h2>Reports & Analytics</h2>
                  <div className="feature-list">
                    <div className="feature-item">
                      <i className="fas fa-chart-bar"></i>
                      <span>Attendance Analytics</span>
                    </div>
                    <div className="feature-item">
                      <i className="fas fa-download"></i>
                      <span>Export Reports</span>
                    </div>
                    <div className="feature-item">
                      <i className="fas fa-calendar-alt"></i>
                      <span>Monthly Reports</span>
                    </div>
                  </div>
                </div>
              </>
            )}

            {activeSection === 'settings' && (
              <>
                <div className="section-description">
                  <p>Configure system settings, manage user accounts, and customize application preferences.</p>
                </div>

                <div className="settings-section">
                  <h2>System Settings</h2>
                  <div className="feature-list">
                    <div className="feature-item">
                      <i className="fas fa-cog"></i>
                      <span>General Settings</span>
                    </div>
                    <div className="feature-item">
                      <i className="fas fa-users-cog"></i>
                      <span>User Management</span>
                    </div>
                    <div className="feature-item">
                      <i className="fas fa-database"></i>
                      <span>Database Configuration</span>
                    </div>
                  </div>
                </div>
              </>
            )}
          </div>
        </div>
      </div>

      {/* Bottom Taskbar */}
      <div className="taskbar">
        <div className="taskbar-left">
          <div className="taskbar-item active">
            <i className="fas fa-home"></i>
          </div>
          <div className="taskbar-item">
            <i className="fas fa-folder"></i>
          </div>
          <div className="taskbar-item">
            <i className="fas fa-terminal"></i>
          </div>
          <div className="taskbar-item">
            <i className="fas fa-globe"></i>
          </div>
          <div className="taskbar-item">
            <i className="fas fa-code"></i>
          </div>
        </div>
        <div className="taskbar-right">
          <div className="taskbar-info">
            <span className="weather-info">
              <i className="fas fa-cloud"></i> Rainy days ahead
            </span>
            <span className="time-info">7:37 PM</span>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
