import React, { useState } from 'react';
import './Dashboard.css';
import './Dashboard-light.css';
import './DatabaseReset.css';
import DatabaseResetComponent from './DatabaseResetComponent';
import DashboardLayout from './DashboardLayout';
import StatsOverview from './StatsOverview';
import StudentManagement from './StudentManagement';
import AdminSettings from './AdminSettings';
import ReportsSection from './ReportsSection';
import { useOTPVerification } from '../hooks/useOTPVerification';
import { useStudentSearch } from '../hooks/useStudentSearch';
import { useDashboardData } from '../hooks/useDashboardData';

const Dashboard = () => {
  const [activeSection, setActiveSection] = useState('overview');
  const [isLightTheme, setIsLightTheme] = useState(false);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);

  // Use custom hooks
  const {
    stats,
    attendanceData,
    lowAttendanceAlert,
    isLoading,
    refreshMessage,
    lastUpdated,
    analyticsData,
    analyticsLoading,
    fetchDashboardData,
    handleExport,
    fetchAnalyticsData
  } = useDashboardData();

  const {
    searchQuery,
    searchResults,
    searchLoading,
    searchMessage,
    handleSearchInputChange,
    setSearchResults
  } = useStudentSearch();

  const {
    showOTPModal,
    otpCode,
    otpLoading,
    otpMessage,
    otpCountdown,
    canResendOTP,
    resendCountdown,
    setShowOTPModal,
    setOtpCode,
    handleVerifyOTP,
    handleResendOTP,
    formatTime
  } = useOTPVerification();
















  return (
    <div className={`dashboard-container ${isLightTheme ? 'light-theme' : ''}`}>
      <DashboardLayout 
        activeSection={activeSection}
        setActiveSection={setActiveSection}
        isLightTheme={isLightTheme}
        setIsLightTheme={setIsLightTheme}
        adminEmail="admin@system.com"
        isMobileMenuOpen={isMobileMenuOpen}
        setIsMobileMenuOpen={setIsMobileMenuOpen}
      >
        <div className="main-content">
        {activeSection === 'overview' && (
          <StatsOverview 
            stats={stats}
            attendanceData={attendanceData}
            lowAttendanceAlert={lowAttendanceAlert}
            isLoading={isLoading}
            refreshMessage={refreshMessage}
            lastUpdated={lastUpdated}
            onRefresh={fetchDashboardData}
            onExport={handleExport}
          />
        )}

        {activeSection === 'students' && (
          <StudentManagement 
            searchQuery={searchQuery}
            searchResults={searchResults}
            searchLoading={searchLoading}
            searchMessage={searchMessage}
            handleSearchInputChange={handleSearchInputChange}
            setSearchResults={setSearchResults}
            fetchDashboardData={fetchDashboardData}
          />
        )}

        {activeSection === 'reports' && (
          <ReportsSection 
            analyticsData={analyticsData}
            analyticsLoading={analyticsLoading}
            fetchAnalyticsData={fetchAnalyticsData}
          />
        )}

        {activeSection === 'settings' && (
          <AdminSettings 
            adminEmail="admin@system.com"
            fetchAdminEmail={() => {}}
          />
        )}

        {activeSection === 'database-reset' && (
          <DatabaseResetComponent fetchDashboardData={fetchDashboardData} />
        )}

        {activeSection === 'attendance' && (
          <div className="attendance-section">
            <div className="content-header">
              <div className="header-left-content">
                <h2>Meal Attendance</h2>
                <div className="date-info">
                  Last Updated: {lastUpdated || new Date().toLocaleString()}
                </div>
              </div>
              <div className="header-actions">
                <button onClick={fetchDashboardData} className="refresh-btn" disabled={isLoading}>
                  <i className={`fas fa-sync-alt ${isLoading ? 'spinning' : ''}`}></i>
                  Refresh
                </button>
              </div>
            </div>

            {/* Stats Cards */}
            <div className="stats-grid">
              <div className="stat-card total-students">
                <h3>
                  <i className="fas fa-users"></i>
                  Total Students
                </h3>
                <p className="stat-number">{stats?.totalStudents || 0}</p>
              </div>

              <div className="stat-card present-today">
                <h3>
                  <i className="fas fa-user-check"></i>
                  Present Today
                </h3>
                <p className="stat-number">{stats?.presentToday || 0}</p>
              </div>

              <div className="stat-card absent-today">
                <h3>
                  <i className="fas fa-user-times"></i>
                  Absent Today
                </h3>
                <p className="stat-number">{stats?.absentToday || 0}</p>
              </div>

              <div className="stat-card attendance-rate">
                <h3>
                  <i className="fas fa-chart-line"></i>
                  Attendance Rate
                </h3>
                <p className="stat-number">{stats?.attendanceRate || '0%'}</p>
              </div>
            </div>

            {/* Recent Attendance Table */}
            <div className="recent-attendance">
              <h3>Recent Attendance Records</h3>
              <div className="table-container">
                <table className="attendance-table">
                  <thead>
                    <tr>
                      <th>Student ID</th>
                      <th>Name</th>
                      <th>Date</th>
                      <th>Time</th>
                    </tr>
                  </thead>
                  <tbody>
                    {attendanceData.length > 0 ? (
                      attendanceData.map((record, index) => (
                        <tr key={index}>
                          <td>{record.studentId}</td>
                          <td>{record.studentName}</td>
                          <td>{new Date(record.date).toLocaleDateString()}</td>
                          <td>{new Date(record.date).toLocaleTimeString()}</td>
                        </tr>
                      ))
                    ) : (
                      <tr>
                        <td colSpan="4" className="no-data">No recent attendance records</td>
                      </tr>
                    )}
                  </tbody>
                </table>
              </div>
            </div>
          </div>
        )}
        </div>
      </DashboardLayout>

      {/* OTP Modal */}
      {showOTPModal && (
        <div className="modal-overlay" onClick={() => setShowOTPModal(false)}>
          <div className="modal-content" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header">
              <h3>
                <i className="fas fa-shield-alt"></i>
                Email Verification
              </h3>
              <button 
                className="modal-close-btn" 
                onClick={() => setShowOTPModal(false)}
              >
                ×
              </button>
            </div>

            <div className="modal-body">
              {otpMessage && (
                <div className={`modal-message ${otpMessage.includes('successful') || otpMessage.includes('sent') ? 'success' : 'error'}`}>
                  {otpMessage}
                </div>
              )}

              <form onSubmit={handleVerifyOTP} className="settings-form">
                <div className="settings-section">
                  <h4>
                    <i className="fas fa-envelope"></i>
                    Enter Verification Code
                  </h4>
                  <p style={{color: '#666', fontSize: '14px', marginBottom: '20px'}}>
                    We've sent a 6-digit verification code to your email
                  </p>

                  <div className="form-group">
                    <label>Verification Code</label>
                    <input
                      type="text"
                      value={otpCode}
                      onChange={(e) => setOtpCode(e.target.value.replace(/\D/g, '').slice(0, 6))}
                      placeholder="Enter 6-digit code"
                      maxLength="6"
                      style={{
                        textAlign: 'center',
                        fontSize: '18px',
                        letterSpacing: '4px',
                        fontFamily: 'monospace'
                      }}
                      required
                    />
                  </div>

                  <div style={{display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginTop: '15px'}}>
                    <div style={{fontSize: '13px', color: '#666'}}>
                      {otpCountdown > 0 ? (
                        <>⏰ Code expires in: <strong>{formatTime(otpCountdown)}</strong></>
                      ) : (
                        <span style={{color: '#e53e3e'}}>⚠️ Code expired</span>
                      )}
                    </div>

                    <button
                      type="button"
                      onClick={handleResendOTP}
                      disabled={!canResendOTP || otpLoading}
                      style={{
                        background: 'none',
                        border: 'none',
                        color: canResendOTP ? '#3182ce' : '#a0aec0',
                        cursor: canResendOTP ? 'pointer' : 'not-allowed',
                        fontSize: '13px',
                        textDecoration: 'underline'
                      }}
                    >
                      {resendCountdown > 0 ? `Resend in ${resendCountdown}s` : 'Resend Code'}
                    </button>
                  </div>
                </div>

                <div className="form-actions">
                  <button
                    type="button"
                    className="btn-secondary"
                    onClick={() => setShowOTPModal(false)}
                    disabled={otpLoading}
                  >
                    Cancel
                  </button>
                  <button
                    type="submit"
                    className="btn-primary"
                    disabled={otpLoading || otpCode.length !== 6 || otpCountdown === 0}
                  >
                    {otpLoading ? (
                      <>
                        <i className="fas fa-spinner fa-spin"></i>
                        Verifying...
                      </>
                    ) : (
                      'Verify & Login'
                    )}
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      )}

    </div>
  );
};

export default Dashboard;
