import React, { useState, useEffect, useRef } from 'react';
import './StudentManagement.css';

const StudentManagement = ({ 
  searchQuery,
  searchResults,
  searchLoading,
  searchMessage,
  handleSearchInputChange,
  setSearchResults,
  fetchDashboardData 
}) => {
  const searchInputRef = useRef(null);

  // Student modals state
  const [showAddStudentModal, setShowAddStudentModal] = useState(false);
  const [showEditStudentModal, setShowEditStudentModal] = useState(false);
  const [showDeleteConfirmModal, setShowDeleteConfirmModal] = useState(false);
  const [showAllStudentsModal, setShowAllStudentsModal] = useState(false);

  // Student data state
  const [newStudentData, setNewStudentData] = useState({
    id: '',
    name: '',
    department: '',
    photoUrl: ''
  });
  const [editingStudent, setEditingStudent] = useState(null);
  const [editStudentData, setEditStudentData] = useState({
    id: '',
    name: '',
    department: '',
    photoUrl: ''
  });
  const [studentToDelete, setStudentToDelete] = useState(null);
  const [allStudents, setAllStudents] = useState([]);

  // Loading and message state
  const [addStudentLoading, setAddStudentLoading] = useState(false);
  const [addStudentMessage, setAddStudentMessage] = useState('');
  const [editStudentLoading, setEditStudentLoading] = useState(false);
  const [editStudentMessage, setEditStudentMessage] = useState('');
  const [deleteLoading, setDeleteLoading] = useState(false);
  const [allStudentsLoading, setAllStudentsLoading] = useState(false);

  // Focus search input when component mounts
  useEffect(() => {
    if (searchInputRef.current) {
      setTimeout(() => {
        searchInputRef.current.focus();
      }, 100);
    }
  }, []);

  // Handle adding new student
  const handleAddStudent = async (e) => {
    e.preventDefault();

    if (!newStudentData.id.trim() || !newStudentData.name.trim()) {
      setAddStudentMessage('Student ID and Name are required fields.');
      return;
    }

    try {
      setAddStudentLoading(true);
      setAddStudentMessage('');

      const response = await fetch('/api/students/add', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(newStudentData),
      });

      const data = await response.json();

      if (response.ok) {
        setAddStudentMessage('Student registered successfully!');
        setNewStudentData({
          id: '',
          name: '',
          department: '',
          photoUrl: ''
        });
        fetchDashboardData();
        setTimeout(() => {
          setAddStudentMessage('');
        }, 2500);
      } else {
        setAddStudentMessage(data.error || 'Failed to register student.');
      }
    } catch (error) {
      setAddStudentMessage('Network error. Please check if the backend server is running.');
    } finally {
      setAddStudentLoading(false);
    }
  };

  // Handle editing student
  const handleEditStudent = (student) => {
    setEditingStudent(student);
    setEditStudentData({
      id: student.id,
      name: student.name,
      department: student.department,
      photoUrl: student.photoUrl || ''
    });
    setShowEditStudentModal(true);
  };

  // Handle updating student
  const handleUpdateStudent = async (e) => {
    e.preventDefault();

    if (!editStudentData.id.trim() || !editStudentData.name.trim()) {
      setEditStudentMessage('Student ID and Name are required fields.');
      return;
    }

    try {
      setEditStudentLoading(true);
      setEditStudentMessage('');

      const response = await fetch(`/api/students/update/${editingStudent._id}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(editStudentData),
      });

      const data = await response.json();

      if (response.ok) {
        setEditStudentMessage('Student updated successfully!');
        setTimeout(() => {
          setShowEditStudentModal(false);
          setEditStudentMessage('');
          setEditingStudent(null);
        }, 2000);
      } else {
        setEditStudentMessage(data.error || 'Failed to update student.');
      }
    } catch (error) {
      setEditStudentMessage('Network error. Please check if the backend server is running.');
    } finally {
      setEditStudentLoading(false);
    }
  };

  // Handle deleting student
  const handleDeleteStudent = (studentId) => {
    setStudentToDelete(studentId);
    setShowDeleteConfirmModal(true);
  };

  // Confirm delete student
  const confirmDeleteStudent = async () => {
    if (!studentToDelete) return;

    try {
      setDeleteLoading(true);
      const response = await fetch(`/api/students/delete/${studentToDelete}`, {
        method: 'DELETE',
        headers: {
          'Content-Type': 'application/json',
        },
      });

      const data = await response.json();

      if (response.ok) {
        fetchDashboardData();
        setShowDeleteConfirmModal(false);
        setStudentToDelete(null);
      }
    } catch (error) {
      console.error('Error deleting student:', error);
    } finally {
      setDeleteLoading(false);
    }
  };

  // Handle viewing all students
  const handleViewAllStudents = async () => {
    try {
      setAllStudentsLoading(true);
      setShowAllStudentsModal(true);

      const response = await fetch('/api/students/all');
      const data = await response.json();

      if (response.ok && data.success) {
        setAllStudents(data.students);
      } else {
        setAllStudents([]);
      }
    } catch (error) {
      setAllStudents([]);
    } finally {
      setAllStudentsLoading(false);
    }
  };

  return (
    <div className="students-section">
      <div className="content-header">
        <div className="header-left-content">
          <h2>Students Management</h2>
          <div className="date-info">
            Manage student records and attendance data
          </div>
        </div>
        <div className="header-actions">
          <button 
            onClick={() => setShowAddStudentModal(true)} 
            className="add-student-btn"
          >
            <i className="fas fa-plus"></i>
            Add Student
          </button>
          <button 
            onClick={handleViewAllStudents} 
            className="refresh-btn"
          >
            <i className="fas fa-users"></i>
            View All Students
          </button>
        </div>
      </div>

      <div className="search-section">
        <div className="search-container">
          <div className="search-input-wrapper">
            <i className="fas fa-search search-icon"></i>
            <input
              ref={searchInputRef}
              type="text"
              className="search-input"
              placeholder="Search Student ID or Name..."
              value={searchQuery}
              onChange={handleSearchInputChange}
            />
            {searchLoading && <i className="fas fa-spinner fa-spin loading-icon"></i>}
          </div>
          {searchMessage && (
            <div className={`search-message ${searchMessage.includes('found') ? 'success' : 'info'}`}>
              {searchMessage}
            </div>
          )}
        </div>

        <div className="action-buttons">
          <button 
            onClick={() => setShowAddStudentModal(true)} 
            className="add-student-btn"
          >
            <i className="fas fa-plus"></i>
            Add New Student
          </button>
          <button 
            onClick={handleViewAllStudents} 
            className="refresh-btn"
          >
            <i className="fas fa-users"></i>
            View All Students
          </button>
        </div>
      </div>

      {/* Search Results */}
      {searchResults.length > 0 && (
        <div className="search-results">
          <h3>Search Results</h3>
          <div className="students-grid">
            {searchResults.map((student) => (
              <div key={student._id} className="student-card">
                <div className="student-photo">
                  <img 
                    src={student.photoUrl || '/images/default-student.png'} 
                    alt="Student Photo"
                    onError={(e) => {
                      e.target.src = '/images/default-student.png';
                    }}
                  />
                </div>
                <div className="student-info">
                  <h4>{student.name}</h4>
                  <p><strong>ID:</strong> {student.id}</p>
                  <p><strong>Department:</strong> {student.department}</p>
                </div>
                <div className="student-actions-card">
                  <button 
                    onClick={() => handleEditStudent(student)}
                    className="edit-btn"
                    title="Edit Student"
                  >
                    <i className="fas fa-edit"></i>
                  </button>
                  <button 
                    onClick={() => handleDeleteStudent(student._id)}
                    className="delete-btn"
                    title="Delete Student"
                  >
                    <i className="fas fa-trash"></i>
                  </button>
                </div>
              </div>
            ))}
          </div>
        </div>
      )}

      {/* Add Student Modal */}
      {showAddStudentModal && (
        <div className="modal-overlay">
          <div className="modal-content">
            <div className="modal-header">
              <h3>Add New Student</h3>
              <button 
                className="modal-close-btn" 
                onClick={() => setShowAddStudentModal(false)}
              >
                ×
              </button>
            </div>

            <div className="modal-body">
              {addStudentMessage && (
                <div className={`modal-message ${addStudentMessage.includes('success') ? 'success' : 'error'}`}>
                  {addStudentMessage}
                </div>
              )}

              <form onSubmit={handleAddStudent} className="student-form">
                <div className="form-group">
                  <label>Student ID *</label>
                  <input
                    type="text"
                    value={newStudentData.id}
                    onChange={(e) => setNewStudentData({...newStudentData, id: e.target.value})}
                    placeholder="Enter student ID"
                    required
                  />
                </div>

                <div className="form-group">
                  <label>Full Name *</label>
                  <input
                    type="text"
                    value={newStudentData.name}
                    onChange={(e) => setNewStudentData({...newStudentData, name: e.target.value})}
                    placeholder="Enter full name"
                    required
                  />
                </div>

                <div className="form-group">
                  <label>Department</label>
                  <input
                    type="text"
                    value={newStudentData.department}
                    onChange={(e) => setNewStudentData({...newStudentData, department: e.target.value})}
                    placeholder="Enter department"
                  />
                </div>

                <div className="form-group">
                  <label>Photo URL</label>
                  <input
                    type="url"
                    value={newStudentData.photoUrl}
                    onChange={(e) => setNewStudentData({...newStudentData, photoUrl: e.target.value})}
                    placeholder="Enter photo URL (optional)"
                  />
                </div>

                <div className="form-actions">
                  <button
                    type="button"
                    className="btn-cancel"
                    onClick={() => setShowAddStudentModal(false)}
                    disabled={addStudentLoading}
                  >
                    Cancel
                  </button>
                  <button
                    type="submit"
                    className="btn-submit"
                    disabled={addStudentLoading}
                  >
                    {addStudentLoading ? (
                      <>
                        <i className="fas fa-spinner fa-spin"></i>
                        Adding...
                      </>
                    ) : (
                      'Add Student'
                    )}
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      )}

      {/* Edit Student Modal */}
      {showEditStudentModal && (
        <div className="modal-overlay">
          <div className="modal-content">
            <div className="modal-header">
              <h3>Edit Student</h3>
              <button 
                className="modal-close-btn" 
                onClick={() => setShowEditStudentModal(false)}
              >
                ×
              </button>
            </div>

            <div className="modal-body">
              {editStudentMessage && (
                <div className={`modal-message ${editStudentMessage.includes('success') ? 'success' : 'error'}`}>
                  {editStudentMessage}
                </div>
              )}

              <form onSubmit={handleUpdateStudent} className="student-form">
                <div className="form-group">
                  <label>Student ID *</label>
                  <input
                    type="text"
                    value={editStudentData.id}
                    onChange={(e) => setEditStudentData({...editStudentData, id: e.target.value})}
                    placeholder="Enter student ID"
                    required
                  />
                </div>

                <div className="form-group">
                  <label>Full Name *</label>
                  <input
                    type="text"
                    value={editStudentData.name}
                    onChange={(e) => setEditStudentData({...editStudentData, name: e.target.value})}
                    placeholder="Enter full name"
                    required
                  />
                </div>

                <div className="form-group">
                  <label>Department</label>
                  <input
                    type="text"
                    value={editStudentData.department}
                    onChange={(e) => setEditStudentData({...editStudentData, department: e.target.value})}
                    placeholder="Enter department"
                  />
                </div>

                <div className="form-group">
                  <label>Photo URL</label>
                  <input
                    type="url"
                    value={editStudentData.photoUrl}
                    onChange={(e) => setEditStudentData({...editStudentData, photoUrl: e.target.value})}
                    placeholder="Enter photo URL (optional)"
                  />
                </div>

                <div className="form-actions">
                  <button
                    type="button"
                    className="btn-cancel"
                    onClick={() => setShowEditStudentModal(false)}
                    disabled={editStudentLoading}
                  >
                    Cancel
                  </button>
                  <button
                    type="submit"
                    className="btn-submit"
                    disabled={editStudentLoading}
                  >
                    {editStudentLoading ? (
                      <>
                        <i className="fas fa-spinner fa-spin"></i>
                        Updating...
                      </>
                    ) : (
                      'Update Student'
                    )}
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      )}

      {/* Delete Confirmation Modal */}
      {showDeleteConfirmModal && (
        <div className="modal-overlay">
          <div className="modal-content delete-modal">
            <div className="modal-header">
              <h3>Confirm Delete</h3>
              <button 
                className="modal-close-btn" 
                onClick={() => setShowDeleteConfirmModal(false)}
              >
                ×
              </button>
            </div>

            <div className="modal-body">
              <div className="delete-warning">
                <i className="fas fa-exclamation-triangle"></i>
                <p>Are you sure you want to delete this student? This action cannot be undone.</p>
              </div>

              <div className="form-actions">
                <button
                  type="button"
                  className="btn-cancel"
                  onClick={() => setShowDeleteConfirmModal(false)}
                  disabled={deleteLoading}
                >
                  Cancel
                </button>
                <button
                  type="button"
                  className="btn-delete"
                  onClick={confirmDeleteStudent}
                  disabled={deleteLoading}
                >
                  {deleteLoading ? (
                    <>
                      <i className="fas fa-spinner fa-spin"></i>
                      Deleting...
                    </>
                  ) : (
                    'Delete Student'
                  )}
                </button>
              </div>
            </div>
          </div>
        </div>
      )}

      {/* All Students Modal */}
      {showAllStudentsModal && (
        <div className="modal-overlay">
          <div className="modal-content large-modal">
            <div className="modal-header">
              <h3>All Students</h3>
              <button 
                className="modal-close-btn" 
                onClick={() => setShowAllStudentsModal(false)}
              >
                ×
              </button>
            </div>

            <div className="modal-body">
              {allStudentsLoading ? (
                <div className="loading-container">
                  <i className="fas fa-spinner fa-spin"></i>
                  <p>Loading students...</p>
                </div>
              ) : (
                <div className="all-students-list">
                  {allStudents.length > 0 ? (
                    <div className="students-grid">
                      {allStudents.map((student) => (
                        <div key={student._id} className="student-card">
                          <div className="student-photo">
                            <img 
                              src={student.photoUrl || '/images/default-student.png'} 
                              alt="Student Photo"
                              onError={(e) => {
                                e.target.src = '/images/default-student.png';
                              }}
                            />
                          </div>
                          <div className="student-info">
                            <h4>{student.name}</h4>
                            <p><strong>ID:</strong> {student.id}</p>
                            <p><strong>Department:</strong> {student.department}</p>
                          </div>
                          <div className="student-actions-card">
                            <button 
                              onClick={() => {
                                setShowAllStudentsModal(false);
                                handleEditStudent(student);
                              }}
                              className="edit-btn"
                              title="Edit Student"
                            >
                              <i className="fas fa-edit"></i>
                            </button>
                            <button 
                              onClick={() => {
                                setShowAllStudentsModal(false);
                                handleDeleteStudent(student._id);
                              }}
                              className="delete-btn"
                              title="Delete Student"
                            >
                              <i className="fas fa-trash"></i>
                            </button>
                          </div>
                        </div>
                      ))}
                    </div>
                  ) : (
                    <div className="no-students">
                      <i className="fas fa-users"></i>
                      <p>No students found in the database.</p>
                    </div>
                  )}
                </div>
              )}
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default StudentManagement;
