import React, { useState, useRef, useEffect, useCallback } from 'react';
import './AttendancePage.css';

const AttendancePage = ({ user, onLogout }) => {
  const [studentId, setStudentId] = useState('');
  const [student, setStudent] = useState(null);
  const [message, setMessage] = useState('');
  const [status, setStatus] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const [mealWindowBlocked, setMealWindowBlocked] = useState(false);
  const [nextMealInfo, setNextMealInfo] = useState('');
  const [mealWindows, setMealWindows] = useState({});
  const inputRef = useRef(null);
  const successAudioRef = useRef(null);
  const errorAudioRef = useRef(null);

  // Fetch meal windows from server
  const fetchMealWindows = async () => {
    try {
      console.log('🔄 Fetching meal windows from API...');
      const response = await fetch('/api/meal-windows');
      if (response.ok) {
        const data = await response.json();
        console.log('📥 API Response:', data);
        if (data.success && data.mealWindows) {
          console.log('✅ Setting meal windows from database:', data.mealWindows);
          console.log('🕐 Current time:', new Date().toLocaleTimeString());

          // Log each meal window with its timing
          Object.entries(data.mealWindows).forEach(([mealType, config]) => {
            if (config.enabled) {
              console.log(`📋 ${mealType}: ${config.startTime}-${config.endTime} (±${config.beforeWindow}/${config.afterWindow}min)`);
            }
          });

          setMealWindows(data.mealWindows);
        } else {
          console.error('❌ Invalid API response structure:', data);
        }
      } else {
        console.error('❌ API request failed:', response.status, response.statusText);
      }
    } catch (error) {
      console.error('❌ Error fetching meal windows:', error);
    }
  };

  // Meal window timing logic
  const checkMealWindow = useCallback(() => {
    // Don't process if meal windows haven't been loaded from database yet
    if (Object.keys(mealWindows).length === 0) {
      console.log('⏳ Meal windows not loaded yet, skipping check');
      setMealWindowBlocked(true);
      return;
    }

    const now = new Date();
    const currentTime = now.getHours() * 60 + now.getMinutes();
    console.log(`🕐 Current time: ${Math.floor(currentTime/60)}:${(currentTime%60).toString().padStart(2,'0')} (${currentTime} minutes)`);

    // Convert meal windows to minutes format with before/after buffers
    const mealWindowsInMinutes = {};
    Object.entries(mealWindows).forEach(([mealType, config]) => {
      if (config.enabled) {
        const [startHour, startMinute] = config.startTime.split(':').map(Number);
        const [endHour, endMinute] = config.endTime.split(':').map(Number);

        const startTime = startHour * 60 + startMinute;
        const endTime = endHour * 60 + endMinute;

        // Apply before/after window buffers
        const windowStart = startTime - (config.beforeWindow || 30);
        const windowEnd = endTime + (config.afterWindow || 30);

        mealWindowsInMinutes[mealType] = {
          start: windowStart,
          end: windowEnd,
          originalStart: startTime,
          originalEnd: endTime
        };

        console.log(`📋 ${mealType}: Window ${Math.floor(windowStart/60)}:${(windowStart%60).toString().padStart(2,'0')}-${Math.floor(windowEnd/60)}:${(windowEnd%60).toString().padStart(2,'0')} (${windowStart}-${windowEnd})`);
      }
    });

    let isInMealWindow = false;
    let nextMealType = '';
    let timeUntilOpen = null;

    // Check if currently in any meal window (including buffers)
    for (const [mealType, window] of Object.entries(mealWindowsInMinutes)) {
      if (currentTime >= window.start && currentTime <= window.end) {
        isInMealWindow = true;
        console.log(`✅ Currently in ${mealType} window`);
        break;
      }
    }

    if (!isInMealWindow) {
      console.log('❌ Not in any meal window - attendance blocked');
    }

    // Find next meal window if not currently in one
    if (!isInMealWindow) {
      for (const [meal, window] of Object.entries(mealWindowsInMinutes)) {
        if (currentTime < window.start) {
          nextMealType = meal;
          timeUntilOpen = window.start - currentTime;
          break;
        }
      }

      // If no meal today, next is breakfast tomorrow
      if (!nextMealType) {
        const breakfastWindow = mealWindowsInMinutes.breakfast;
        if (breakfastWindow) {
          nextMealType = 'breakfast';
          timeUntilOpen = breakfastWindow.start + (24 * 60) - currentTime;
        }
      }
    }

    setMealWindowBlocked(!isInMealWindow);

    if (timeUntilOpen) {
      const hours = Math.floor(timeUntilOpen / 60);
      const minutes = timeUntilOpen % 60;
      const nextWindowTime = new Date();
      nextWindowTime.setMinutes(nextWindowTime.getMinutes() + timeUntilOpen);
      const timeString = nextWindowTime.toLocaleTimeString([], {hour: '2-digit', minute:'2-digit'});

      let timeText;
      if (hours > 0) {
        timeText = `${hours}h ${minutes}m (${timeString})`;
      } else {
        timeText = `${minutes}m (${timeString})`;
      }

      setNextMealInfo(`${nextMealType.charAt(0).toUpperCase() + nextMealType.slice(1)} window opens in ${timeText}`);
    }
  }, [mealWindows]);

  useEffect(() => {
    // Focus on input when component mounts
    if (inputRef.current) {
      inputRef.current.focus();
    }

    // Fetch meal windows from server
    fetchMealWindows();

    // Check meal window status
    checkMealWindow();
    const interval = setInterval(checkMealWindow, 60000);

    return () => clearInterval(interval);
  }, []);

  // Re-check meal window when mealWindows state changes
  useEffect(() => {
    checkMealWindow();
  }, [mealWindows, checkMealWindow]);

  const playSound = (isSuccess) => {
    try {
      if (isSuccess && successAudioRef.current) {
        successAudioRef.current.play();
      } else if (!isSuccess && errorAudioRef.current) {
        errorAudioRef.current.play();
      }
    } catch (error) {
      console.log('Audio playback failed:', error);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!studentId.trim()) return;

    // Check if meal window is blocked
    if (mealWindowBlocked) {
      setMessage(`❌ Attendance blocked: ${nextMealInfo}`);
      playSound(false);
      return;
    }

    setIsLoading(true);
    setMessage('');
    setStudent(null);

    try {
      const response = await fetch('/api/attendance', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ studentId: studentId.trim() }),
      });

      const data = await response.json();

      if (response.ok) {
        setStudent(data.student);
        setStatus(data.status);

        switch (data.status) {
          case 'allowed':
            setMessage('✅ Meal allowed! Welcome!');
            playSound(true);
            break;
          case 'already_used':
            setMessage('❌ Meal already used for this period');
            playSound(false);
            break;
          case 'invalid':
            setMessage('❌ Student ID not found');
            playSound(false);
            break;
          case 'blocked':
            setMessage(`❌ ${data.message || 'Meal window is closed'}`);
            playSound(false);
            break;
          case 'error':
            setMessage(`❌ ${data.message || 'System error occurred'}`);
            playSound(false);
            break;
          default:
            setMessage('❌ Unknown status');
            playSound(false);
        }
      } else {
        setMessage('❌ System error. Please try again.');
        playSound(false);
      }
    } catch (error) {
      setMessage('❌ Network error. Please check connection.');
      playSound(false);
    } finally {
      setIsLoading(false);
      setStudentId('');
      // Refocus input for next scan
      setTimeout(() => {
        if (inputRef.current) {
          inputRef.current.focus();
        }
      }, 100);
    }
  };

  const handleLogout = async () => {
    try {
      await fetch('/api/logout', { method: 'POST' });
      onLogout();
    } catch (error) {
      console.error('Logout error:', error);
      onLogout(); // Logout anyway
    }
  };

  return (
    <div className="attendance-container">
      <div className="attendance-header">
        <div className="header-left">
          <img src="/images/salale_university_logo.png" alt="Salale University" />
          <h1>Salale University</h1>
        </div>
        <button onClick={handleLogout} className="logout-btn">
          <i className="fas fa-sign-out-alt"></i>
          Logout
        </button>
      </div>

      <div className="attendance-content">
        <div className="attendance-layout">
          {/* Left side: Input form */}
          <div className="input-section">
            <div className="input-card">
              {mealWindowBlocked && (
                <div className="meal-window-warning">
                  <i className="fas fa-clock"></i>
                  <span>{nextMealInfo}</span>
                </div>
              )}
              <form onSubmit={handleSubmit}>
                <label htmlFor="studentId">Student ID (Manual or Scan Barcode):</label>
                <input
                  ref={inputRef}
                  type="text"
                  id="studentId"
                  value={studentId}
                  onChange={(e) => setStudentId(e.target.value)}
                  placeholder={mealWindowBlocked ? "Meal window closed" : "Enter ID"}
                  autoComplete="off"
                  disabled={mealWindowBlocked}
                  required
                />
                <button type="submit" className="check-btn" disabled={isLoading || mealWindowBlocked}>
                  {mealWindowBlocked ? 'Meal Window Closed' : isLoading ? 'Checking...' : 'Check Attendance'}
                </button>
              </form>

              {message && (
                <div className={`attendance-message ${status}`}>
                  {message}
                </div>
              )}
            </div>
          </div>

          {/* Right side: Student information */}
          <div className="result-section">
            {student && (
              <div className="student-card">
                <div className={`status-message ${status}`}>
                  {status === 'allowed' ? '✅ ALLOWED' : 
                   status === 'already_used' ? '❌ ALREADY USED' : 
                   '❌ DENIED'}
                </div>
                <div className="student-info-row">
                  <div className="student-details">
                    <div><strong>Name:</strong> {student.name}</div>
                    <div><strong>ID Number:</strong> {student.id}</div>
                    <div><strong>Program:</strong> {student.department}</div>
                  </div>

                  <div className="student-photo">
                    <img 
                      src={student.photoUrl || '/images/default-student.png'} 
                      alt="Student Photo"
                      onError={(e) => {
                        e.target.src = '/images/default-student.png';
                      }}
                    />
                  </div>
                </div>
              </div>
            )}
          </div>
        </div>
      </div>

      {/* Audio elements */}
      <audio ref={successAudioRef} preload="auto">
        <source src="/sounds/success.mp3" type="audio/mpeg" />
      </audio>
      <audio ref={errorAudioRef} preload="auto">
        <source src="/sounds/error.mp3" type="audio/mpeg" />
      </audio>
    </div>
  );
};

export default AttendancePage;
