import React, { useState } from 'react';
import './LoginPage.css';

const PasswordResetPage = ({ onBackToLogin }) => {
  const [currentStep, setCurrentStep] = useState(1); // 1: email, 2: verify
  const [email, setEmail] = useState('');
  const [resetCode, setResetCode] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [showNewPassword, setShowNewPassword] = useState(false);
  const [showConfirmPassword, setShowConfirmPassword] = useState(false);
  const [message, setMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  // Step 1: Request password reset
  const handleRequestReset = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setMessage('');

    try {
      const response = await fetch('/api/admin/request-password-reset', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ email })
      });

      const data = await response.json();

      if (data.success) {
        setMessage(`Reset code sent to ${data.email}`);
        setTimeout(() => {
          setCurrentStep(2);
          setMessage('');
        }, 1500);
      } else {
        setMessage(data.message || 'Failed to send reset code');
      }
    } catch (error) {
      console.error('Reset request error:', error);
      setMessage('Network error. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  // Step 2: Verify code and reset password
  const handleVerifyReset = async (e) => {
    e.preventDefault();

    if (newPassword !== confirmPassword) {
      setMessage('Passwords do not match');
      return;
    }

    if (newPassword.length < 6) {
      setMessage('Password must be at least 6 characters long');
      return;
    }

    setIsLoading(true);
    setMessage('');

    try {
      const response = await fetch('/api/admin/verify-password-reset', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({
          email: email,
          otp: resetCode,
          newPassword: newPassword
        })
      });

      const data = await response.json();

      if (data.success) {
        setMessage('Password reset successfully! Redirecting to login...');
        setTimeout(() => {
          onBackToLogin();
        }, 2000);
      } else {
        setMessage(data.message || 'Failed to reset password');
      }
    } catch (error) {
      console.error('Reset verification error:', error);
      setMessage('Network error. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  return (
    <div className="login-container">
      <div className="head-row">
        <div className="header-left">
          <img src="/images/salale_university_logo.png" alt="Salale University" />
          <h1>Salale University</h1>
        </div>
        <i className="material-icons cafeteria-icon">restaurant</i>
      </div>

      <div className="container">
        <div className="login-box">
          <div className="login-header">
            <h2>Reset Password</h2>
            <p>
              {currentStep === 1 
                ? 'Enter your admin email to receive a reset code'
                : 'Enter the code sent to your email and set a new password'
              }
            </p>
          </div>

          {/* Step 1: Request Reset */}
          {currentStep === 1 && (
            <form onSubmit={handleRequestReset}>
              <label htmlFor="email">Admin Email:</label>
              <input
                type="email"
                id="email"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
                placeholder="Enter your admin email"
                required
              />

              <button type="submit" className="login-button" disabled={isLoading}>
                {isLoading ? 'Sending...' : 'Send Reset Code'}
              </button>
            </form>
          )}

          {/* Step 2: Verify Code and Set New Password */}
          {currentStep === 2 && (
            <form onSubmit={handleVerifyReset}>
              <label htmlFor="resetCode">Reset Code:</label>
              <input
                type="text"
                id="resetCode"
                value={resetCode}
                onChange={(e) => setResetCode(e.target.value.replace(/\D/g, '').substring(0, 6))}
                placeholder="Enter 6-digit code"
                maxLength="6"
                required
              />

              <label htmlFor="newPassword">New Password:</label>
              <div className="password-wrapper">
                <input
                  type={showNewPassword ? 'text' : 'password'}
                  id="newPassword"
                  value={newPassword}
                  onChange={(e) => setNewPassword(e.target.value)}
                  placeholder="Enter new password"
                  required
                />
                <i
                  className={`fas ${showNewPassword ? 'fa-eye-slash' : 'fa-eye'} password-toggle`}
                  onClick={() => setShowNewPassword(!showNewPassword)}
                ></i>
              </div>

              <label htmlFor="confirmPassword">Confirm Password:</label>
              <div className="password-wrapper">
                <input
                  type={showConfirmPassword ? 'text' : 'password'}
                  id="confirmPassword"
                  value={confirmPassword}
                  onChange={(e) => setConfirmPassword(e.target.value)}
                  placeholder="Confirm new password"
                  required
                />
                <i
                  className={`fas ${showConfirmPassword ? 'fa-eye-slash' : 'fa-eye'} password-toggle`}
                  onClick={() => setShowConfirmPassword(!showConfirmPassword)}
                ></i>
              </div>

              <button type="submit" className="login-button" disabled={isLoading}>
                {isLoading ? 'Resetting...' : 'Reset Password'}
              </button>
            </form>
          )}

          <div className="login-footer">
            <a href="#" onClick={(e) => { e.preventDefault(); onBackToLogin(); }} className="forgot-password-link">
              <i className="fas fa-arrow-left"></i> Back to Login
            </a>
          </div>

          {message && (
            <div className={`login-message ${message.includes('successfully') || message.includes('sent') ? 'success' : 'error'}`}>
              {message}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default PasswordResetPage;
