                    <button 
                      type="button"
                      className="reset-btn breakfast-btn"
                      onClick={() => handleManualReset('breakfast')}
                      disabled={resetLoading.breakfast}
                    >
                      {resetLoading.breakfast ? (
                        <>
                          <i className="fas fa-spinner fa-spin"></i>
                          Resetting...
                        </>
                      ) : (
                        <>
                          <i className="fas fa-coffee"></i>
                          Reset Breakfast
                        </>
                      )}
                    </button>
                    <button 
                      type="button"
                      className="reset-btn lunch-btn"
                      onClick={() => handleManualReset('lunch')}
                      disabled={resetLoading.lunch}
                    >
                      {resetLoading.lunch ? (
                        <>
                          <i className="fas fa-spinner fa-spin"></i>
                          Resetting...
                        </>
                      ) : (
                        <>
                          <i className="fas fa-utensils"></i>
                          Reset Lunch
                        </>
                      )}
                    </button>
                    <button 
                      type="button"
                      className="reset-btn dinner-btn"
                      onClick={() => handleManualReset('dinner')}
                      disabled={resetLoading.dinner}
                    >
                      {resetLoading.dinner ? (
                        <>
                          <i className="fas fa-spinner fa-spin"></i>
                          Resetting...
                        </>
                      ) : (
                        <>
                          <i className="fas fa-drumstick-bite"></i>
                          Reset Dinner
                        </>
                      )}
                    </button>
                    <button 
                      type="button"
                      className="reset-btn latenight-btn"
                      onClick={() => handleManualReset('lateNight')}
                      disabled={resetLoading.lateNight}
                    >
                      {resetLoading.lateNight ? (
                        <>
                          <i className="fas fa-spinner fa-spin"></i>
                          Resetting...
                        </>
                      ) : (
                        <>
                          <i className="fas fa-moon"></i>
                          Reset Late Night
                        </>
                      )}
                    </button>
                  </div>
                  <div className="settings-note">
                    <p><i className="fas fa-exclamation-triangle"></i> <strong>Warning:</strong> Manual reset will immediately clear all attendance data for the selected meal period.</p>
                  </div>
                </div>

                <div className="form-actions">
                  <button 
                    type="button" 
                    className="btn-cancel"
                    onClick={() => setShowGeneralSettingsModal(false)}
                  >
                    Cancel
                  </button>
                  <button 
                    type="submit" 
                    className="btn-register"
                    disabled={settingsLoading}
                  >
                    {settingsLoading ? (
                      <>
                        <i className="fas fa-spinner fa-spin"></i>
                        Saving...
                      </>
                    ) : (
                      <>
                        <i className="fas fa-save"></i>
                        Save Settings
                      </>
                    )}
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      )}

      {/* View All Students Modal */}
      {showAllStudentsModal && (
        <div className="modal-overlay" onClick={() => setShowAllStudentsModal(false)}>
          <div className="modal-content all-students-modal" onClick={(e) => e.stopPropagation()}>
            <div className="modal-header">
              <h2>All Students</h2>
              <button 
                className="modal-close-btn" 
                onClick={() => setShowAllStudentsModal(false)}
              >
              <i className="fas fa-times"></i>
              </button>
            </div>

            <div className="modal-body">
              {allStudentsLoading ? (
                <div className="loading-container">
                  <i className="fas fa-spinner fa-spin"></i>
                  <p>Loading students...</p>
                </div>
              ) : (
                <div className="students-table-container">
                  <div className="students-count">
                    Total Students: <strong>{allStudents.length}</strong>
                  </div>

                  <div className="students-table-wrapper">
                    <table className="students-table">
                      <thead>
                        <tr>
                          <th>Student ID</th>
                          <th>Name</th>
                          <th>Department</th>
                          <th>Photo</th>
                        </tr>
                      </thead>
                      <tbody>
                        {allStudents.length > 0 ? (
                          allStudents.map((student) => (
                            <tr key={student._id}>
                              <td>{student.id}</td>
                              <td>{student.name}</td>
                              <td>{student.department || 'N/A'}</td>
                              <td>
                                {student.photoUrl ? (
                                  <img 
                                    src={student.photoUrl} 
                                    alt={student.name}
                                    className="student-photo-small"
                                    onError={(e) => {
                                      e.target.src = '/images/default-student.png';
                                    }}
                                  />
                                ) : (
                                  <div className="no-photo">No Photo</div>
                                )}
                              </td>
                            </tr>
                          ))
                        ) : (
                          <tr>
                            <td colSpan="4" className="no-data">No students found</td>
                          </tr>
                        )}
                      </tbody>
                    </table>
                  </div>
                </div>
              )}
            </div>
          </div>
        </div>
      )}

      {/* Bottom Taskbar */}
      <div className="taskbar">
      </div>
    </div>
  );
};

export default Dashboard;
