import React, { useState, useEffect } from 'react';
import './LoginPage.css';

const LoginPage = ({ onLogin }) => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [showPassword, setShowPassword] = useState(false);
  const [message, setMessage] = useState('');
  const [isLoading, setIsLoading] = useState(false);

  // Admin email verification states
  const [showEmailModal, setShowEmailModal] = useState(false);
  const [adminEmail, setAdminEmail] = useState('');
  const [emailLoading, setEmailLoading] = useState(false);
  const [emailMessage, setEmailMessage] = useState('');

  // OTP verification states
  const [showOTPModal, setShowOTPModal] = useState(false);
  const [otpCode, setOtpCode] = useState('');
  const [otpLoading, setOtpLoading] = useState(false);
  const [otpMessage, setOtpMessage] = useState('');
  const [otpCountdown, setOtpCountdown] = useState(300); // 5 minutes
  const [canResendOTP, setCanResendOTP] = useState(false);
  const [resendCountdown, setResendCountdown] = useState(0);
  const [maskedEmail, setMaskedEmail] = useState('');

  // OTP countdown timer
  useEffect(() => {
    let interval;
    if (showOTPModal && otpCountdown > 0) {
      interval = setInterval(() => {
        setOtpCountdown(prev => {
          if (prev <= 1) {
            setOtpMessage('Verification code expired. Please request a new one.');
            return 0;
          }
          return prev - 1;
        });
      }, 1000);
    }
    return () => clearInterval(interval);
  }, [showOTPModal, otpCountdown]);

  // Resend OTP countdown
  useEffect(() => {
    let interval;
    if (resendCountdown > 0) {
      interval = setInterval(() => {
        setResendCountdown(prev => {
          if (prev <= 1) {
            setCanResendOTP(true);
            return 0;
          }
          return prev - 1;
        });
      }, 1000);
    }
    return () => clearInterval(interval);
  }, [resendCountdown]);

  // Format countdown time
  const formatTime = (seconds) => {
    const mins = Math.floor(seconds / 60);
    const secs = seconds % 60;
    return `${mins}:${secs.toString().padStart(2, '0')}`;
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setIsLoading(true);
    setMessage('');

    try {
      // First check if admin credentials (username/password only)
      const adminCheckResponse = await fetch('/api/admin/check-credentials', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ username, password }),
      });

      const adminCheckData = await adminCheckResponse.json();

      if (adminCheckResponse.ok && adminCheckData.success) {
        // Admin credentials valid - show email prompt
        setMessage('Admin credentials verified. Please enter your email.');
        setShowEmailModal(true);
      } else {
        // Try regular staff login
        const response = await fetch('/api/login', {
          method: 'POST',
          headers: {
            'Content-Type': 'application/json',
          },
          body: JSON.stringify({ username, password }),
        });

        const data = await response.json();

        if (response.ok && data.success) {
          setMessage('Login successful!');
          onLogin(data.user);
        } else {
          setMessage(data.error || 'Login failed');
        }
      }
    } catch (error) {
      setMessage('Network error. Please try again.');
    } finally {
      setIsLoading(false);
    }
  };

  // Handle OTP verification
  const handleVerifyOTP = async (e) => {
    e.preventDefault();

    if (!otpCode || otpCode.length !== 6) {
      setOtpMessage('Please enter a valid 6-digit verification code');
      return;
    }

    try {
      setOtpLoading(true);
      setOtpMessage('');

      const response = await fetch('/api/admin/verify-otp', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ otp: otpCode })
      });

      const data = await response.json();

      if (data.success) {
        setOtpMessage('Login successful! Redirecting...');

        // Close OTP modal and login
        setTimeout(() => {
          setShowOTPModal(false);
          setOtpCode('');
          setOtpMessage('');
          onLogin(data.user);
        }, 1500);
      } else {
        setOtpMessage(data.message || 'Invalid verification code');
      }
    } catch (error) {
      console.error('Error verifying OTP:', error);
      setOtpMessage('Network error. Please try again.');
    } finally {
      setOtpLoading(false);
    }
  };

  // Handle email verification for admin
  const handleEmailVerification = async (e) => {
    e.preventDefault();

    if (!adminEmail) {
      setEmailMessage('Please enter your email address');
      return;
    }

    try {
      setEmailLoading(true);
      setEmailMessage('');

      const response = await fetch('/api/admin/send-otp', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ username, password, email: adminEmail })
      });

      const data = await response.json();

      if (data.success) {
        setEmailMessage('Verification code sent to your email');
        setMaskedEmail(data.email);
        // Hide email section and show OTP section after delay
        setTimeout(() => {
          setShowEmailModal(false);
          setTimeout(() => {
            setShowOTPModal(true);
          }, 300);
        }, 1500);
        setOtpCountdown(300);
        setCanResendOTP(false);
        setResendCountdown(60);
      } else {
        setEmailMessage(data.message || 'Invalid email address');
      }
    } catch (error) {
      console.error('Error verifying email:', error);
      setEmailMessage('Network error. Please try again.');
    } finally {
      setEmailLoading(false);
    }
  };

  // Handle resend OTP
  const handleResendOTP = async () => {
    try {
      setOtpLoading(true);
      setOtpMessage('');

      const response = await fetch('/api/admin/resend-otp', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        }
      });

      const data = await response.json();

      if (data.success) {
        setOtpMessage('New verification code sent to your email');
        setOtpCountdown(300);
        setCanResendOTP(false);
        setResendCountdown(60);
      } else {
        setOtpMessage(data.message || 'Failed to resend code');
      }
    } catch (error) {
      console.error('Error resending OTP:', error);
      setOtpMessage('Network error. Please try again.');
    } finally {
      setOtpLoading(false);
    }
  };

  return (
    <div className="login-container">
      <div className="head-row">
        <img src="/images/salale_university_logo.png" alt="Salale University" />
        <h1>Salale University</h1>
        <i className="material-icons cafeteria-icon">restaurant</i>
      </div>

      <div className="container">
        {/* <div className="header">
          <img src="/images/salale_university_logo.png" alt="Salale University Logo" />
          <h2>Salale University Meal <br />Attendance System</h2>
        </div> */}

        <div className="login-box">
          {/* Login Form */}
          {!showEmailModal && !showOTPModal && (
            <>
              <form onSubmit={handleSubmit}>
                <label htmlFor="username">Username:</label>
                <input
                  type="text"
                  id="username"
                  value={username}
                  onChange={(e) => setUsername(e.target.value)}
                  placeholder="Enter username"
                  required
                />

                <label htmlFor="password">Password:</label>
                <div className="password-wrapper">
                  <input
                    type={showPassword ? 'text' : 'password'}
                    id="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    placeholder="Enter password"
                    required
                  />
                  <i
                    className={`fas ${showPassword ? 'fa-eye-slash' : 'fa-eye'} password-toggle`}
                    onClick={() => setShowPassword(!showPassword)}
                  ></i>
                </div>

                <button type="submit" className="login-button" disabled={isLoading}>
                  {isLoading ? 'Logging...' : 'Login'}
                </button>
              </form>

              {message && (
                <div className={`login-message ${message.includes('successful') || message.includes('sent') ? 'success' : 'error'}`}>
                  {message}
                </div>
              )}
            </>
          )}

          {/* Email Verification Section */}
          {showEmailModal && (
            <div className="verification-content">
              <div className="section-header">
                <h3>
                  <i className="fas fa-envelope"></i>
                  Admin Email Verification
                </h3>
                <button className="close-btn" onClick={() => setShowEmailModal(false)}>Ã—</button>
              </div>
              <div className="section-body">
                {emailMessage && (
                  <div className={`message ${emailMessage.includes('sent') ? 'success' : 'error'}`}>
                    {emailMessage}
                  </div>
                )}
                <div className="form-group">
                  <label>ðŸ“§ Enter Your Admin Email</label>
                  <p className="form-description">Please enter the email address associated with your admin account</p>
                  <div className="input-group">
                    <label htmlFor="adminEmail">Email Address</label>
                    <input
                      type="email"
                      id="adminEmail"
                      value={adminEmail}
                      onChange={(e) => setAdminEmail(e.target.value)}
                      placeholder="Enter your admin email"
                      required
                    />
                  </div>
                  <div className="modal-actions">
                    <button 
                      type="button" 
                      className="cancel-btn"
                      onClick={() => setShowEmailModal(false)}
                    >
                      Cancel
                    </button>
                    <button 
                      type="button" 
                      className="submit-btn"
                      onClick={(e) => handleEmailVerification(e)}
                      disabled={emailLoading}
                    >
                      {emailLoading ? 'Sending...' : 'Send Verification Code'}
                    </button>
                  </div>
                </div>
              </div>
            </div>
          )}

          {/* OTP Verification Section */}
          {showOTPModal && (
            <div className="verification-content">
              <div className="section-header">
                <h3>
                  <i className="fas fa-shield-alt"></i>
                  Admin Verification
                </h3>
                <button className="close-btn" onClick={() => setShowOTPModal(false)}>Ã—</button>
              </div>
              <div className="section-body">
                {otpMessage && (
                  <div className={`message ${otpMessage.includes('successful') ? 'success' : 'error'}`}>
                    {otpMessage}
                  </div>
                )}
                <div className="otp-form">
                  <div className="otp-section">
                    <div className="input-group">
                      <label htmlFor="otp">Enter verification Code</label>
                      <input
                        type="text"
                        id="otp"
                        value={otpCode}
                        onChange={(e) => setOtpCode(e.target.value)}
                        placeholder="Enter 6-digit code"
                        maxLength="6"
                        required
                      />
                    </div>

                    <div className="otp-timer">
                      {otpCountdown > 0 ? (
                        <p>Code expires in: {Math.floor(otpCountdown / 60)}:{(otpCountdown % 60).toString().padStart(2, '0')}</p>
                      ) : (
                        <p className="expired">Code has expired. Please request a new one.</p>
                      )}
                    </div>

                    <div className="modal-actions">
                      <button 
                        type="button" 
                        className="cancel-btn"
                        onClick={() => setShowOTPModal(false)}
                      >
                        Cancel
                      </button>
                      <button 
                        type="button" 
                        className="submit-btn"
                        onClick={(e) => handleVerifyOTP(e)}
                        disabled={otpLoading || otpCountdown <= 0}
                      >
                        {otpLoading ? 'Verifying...' : 'Verify Code'}
                      </button>
                    </div>

                    <div className="resend-section">
                      {canResendOTP ? (
                        <button 
                          type="button" 
                          className="resend-btn"
                          onClick={handleResendOTP}
                        >
                          Resend Code
                        </button>
                      ) : (
                        <p>Resend available in {resendCountdown}s</p>
                      )}
                    </div>
                  </div>
                </div>
              </div>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default LoginPage;
