import React, { useState, useEffect, useRef } from 'react';
import './Dashboard.css';
import './Dashboard-light.css';
import './DatabaseReset.css';

const Dashboard = ({ user, onLogout }) => {
  const [attendanceData, setAttendanceData] = useState([]);
  const [stats, setStats] = useState({
    totalStudents: 0,
    todayAttendance: 0,
    weeklyAttendance: 0,
    attendancePercentage: 0
  });
  const [lowAttendanceAlert, setLowAttendanceAlert] = useState(null);
  const [securityNotifications, setSecurityNotifications] = useState([]);
  const [notificationFilter, setNotificationFilter] = useState('all');
  const [isLoading, setIsLoading] = useState(true);
  const [activeSection, setActiveSection] = useState('overview');
  const [refreshMessage, setRefreshMessage] = useState('');
  const [lastUpdated, setLastUpdated] = useState('');
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [analyticsData, setAnalyticsData] = useState(null);
  const [analyticsLoading, setAnalyticsLoading] = useState(false);
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [searchLoading, setSearchLoading] = useState(false);
  const [searchMessage, setSearchMessage] = useState('');
  const [showAddStudentModal, setShowAddStudentModal] = useState(false);
  const [newStudentData, setNewStudentData] = useState({
    id: '',
    name: '',
    department: '',
    photoUrl: ''
  });
  const [addStudentLoading, setAddStudentLoading] = useState(false);
  const [addStudentMessage, setAddStudentMessage] = useState('');
  const [showAddStaffModal, setShowAddStaffModal] = useState(false);
  const [newStaffData, setNewStaffData] = useState({
    username: '',
    password: ''
  });
  const [addStaffLoading, setAddStaffLoading] = useState(false);
  const [addStaffMessage, setAddStaffMessage] = useState('');
  const [showEditStudentModal, setShowEditStudentModal] = useState(false);
  const [editingStudent, setEditingStudent] = useState(null);
  const [editStudentData, setEditStudentData] = useState({
    id: '',
    name: '',
    department: '',
    photoUrl: ''
  });
  const [editStudentLoading, setEditStudentLoading] = useState(false);
  const [editStudentMessage, setEditStudentMessage] = useState('');
  const [showDeleteConfirmModal, setShowDeleteConfirmModal] = useState(false);
  const [studentToDelete, setStudentToDelete] = useState(null);
  const [deleteLoading, setDeleteLoading] = useState(false);
  const [showAdminCredentialsModal, setShowAdminCredentialsModal] = useState(false);
  const [adminCredentials, setAdminCredentials] = useState({
    currentPassword: '',
    newUsername: '',
    newPassword: '',
    confirmPassword: '',
    email: ''
  });
  const [adminCredentialsLoading, setAdminCredentialsLoading] = useState(false);
  const [adminCredentialsMessage, setAdminCredentialsMessage] = useState('');

  // OTP verification states
  const [showOTPModal, setShowOTPModal] = useState(false);
  const [otpCode, setOtpCode] = useState('');
  const [otpLoading, setOtpLoading] = useState(false);
  const [otpMessage, setOtpMessage] = useState('');
  const [otpCountdown, setOtpCountdown] = useState(300); // 5 minutes
  const [canResendOTP, setCanResendOTP] = useState(false);
  const [resendCountdown, setResendCountdown] = useState(0);

  // Database Configuration states
  const [showDatabaseConfigModal, setShowDatabaseConfigModal] = useState(false);
  const [databaseConfig, setDatabaseConfig] = useState({
    connectionString: 'mongodb://localhost:27017/meal_attendance',
    backupSchedule: 'daily',
    backupTime: '02:00',
    dataRetentionMonths: 24,
    archiveAfterMonths: 12,
    connectionPoolSize: 10,
    connectionTimeout: 30,
    cacheEnabled: true,
    cacheDurationMinutes: 30,
    indexOptimization: true,
    gdprCompliance: true
  });
  const [databaseConfigLoading, setDatabaseConfigLoading] = useState(false);
  const [databaseConfigMessage, setDatabaseConfigMessage] = useState('');
  const [adminEmail, setAdminEmail] = useState('');

  // Create ref for search input
  const searchInputRef = useRef(null);

  // Meal Windows state
  const [mealWindows, setMealWindows] = useState({
    breakfast: {
      startTime: '06:00',
      endTime: '09:00',
      beforeWindow: 30,
      afterWindow: 30,
      enabled: true
    },
    lunch: {
      startTime: '11:00',
      endTime: '14:00',
      beforeWindow: 30,
      afterWindow: 30,
      enabled: true
    },
    dinner: {
      startTime: '16:00',
      endTime: '20:00',
      beforeWindow: 30,
      afterWindow: 30,
      enabled: true
    },
    lateNight: {
      startTime: '01:00',
      endTime: '05:30',
      beforeWindow: 15,
      afterWindow: 15,
      enabled: true
    }
  });
  const [mealWindowsLoading, setMealWindowsLoading] = useState(false);
  const [mealWindowsMessage, setMealWindowsMessage] = useState('');
  const [showAllStudentsModal, setShowAllStudentsModal] = useState(false);
  const [allStudents, setAllStudents] = useState([]);
  const [allStudentsLoading, setAllStudentsLoading] = useState(false);
  const [headerShrunk, setHeaderShrunk] = useState(false);
  const [isLightTheme, setIsLightTheme] = useState(false);

  // Fetch security notifications
  const fetchSecurityNotifications = async () => {
    try {
      const response = await fetch('/api/security/notifications');
      if (response.ok) {
        const data = await response.json();
        if (data.success) {
          setSecurityNotifications(data.notifications);
        }
      }
    } catch (error) {
      console.error('Error fetching security notifications:', error);
    }
  };

  // Fetch admin email from database
  const fetchAdminEmail = async () => {
    try {
      const response = await fetch('/api/admin/profile', {
        method: 'GET',
        credentials: 'include', // Include session cookies
        headers: {
          'Content-Type': 'application/json',
        }
      });

      if (response.ok) {
        const data = await response.json();
        console.log('Admin profile data:', data); // Debug log
        setAdminEmail(data.email || '');
      } else {
        console.error('Failed to fetch admin profile:', response.status, response.statusText);
        // Fallback: try to get email from user session if available
        if (user && user.email) {
          setAdminEmail(user.email);
        }
      }
    } catch (error) {
      console.error('Error fetching admin email:', error);
      // Fallback: try to get email from user session if available
      if (user && user.email) {
        setAdminEmail(user.email);
      }
    }
  };

  useEffect(() => {
    fetchDashboardData();
    fetchSecurityNotifications();
    fetchAdminEmail();

    // Initialize database reset functionality directly
    const initDatabaseReset = () => {
      if (window.databaseResetManager) return;

      // Import and initialize the database reset functionality inline
      class DatabaseResetManager {
        constructor() {
          this.isLoading = false;
          this.selectedResetType = 'manual';
          this.resetSchedule = {
            afterLateNight: '05:45',
            afterBreakfast: '09:30', 
            afterLunch: '14:30',
            afterDinner: '20:30'
          };
        }

        init() {
          console.log('DatabaseResetManager init() called');
          this.bindEvents();
          this.updateScheduleDisplay();
        }

        bindEvents() {
          document.addEventListener('click', (e) => {
            if (e.target.closest('.manual-reset-btn')) {
              e.preventDefault();
              console.log('Manual reset button clicked');
              this.showResetConfirmation();
            }
          });

          document.addEventListener('click', (e) => {
            if (e.target.closest('.test-reset-btn')) {
              e.preventDefault();
              this.performTestReset();
            }
          });

          document.addEventListener('click', (e) => {
            if (e.target.closest('.confirm-reset-btn')) {
              this.performManualReset();
            }
            if (e.target.closest('.cancel-reset-btn') || e.target.closest('.reset-confirmation-modal')) {
              if (e.target === e.target.closest('.reset-confirmation-modal') || e.target.closest('.cancel-reset-btn')) {
                this.hideResetConfirmation();
              }
            }
          });
        }

        showResetConfirmation() {
          const modal = document.createElement('div');
          modal.className = 'reset-confirmation-modal';
          modal.innerHTML = `
            <div class="reset-confirmation-content">
              <div class="reset-confirmation-header">
                <i class="fas fa-exclamation-triangle"></i>
                <h3>Confirm Database Reset</h3>
              </div>
              <div class="reset-confirmation-message">
                <p><strong>Warning:</strong> This action will permanently delete all current meal attendance records.</p>
                <p>Students will be able to use their meals again after the reset.</p>
                <p>This action cannot be undone. Are you sure you want to proceed?</p>
              </div>
              <div class="reset-confirmation-actions">
                <button class="reset-btn secondary cancel-reset-btn">
                  <i class="fas fa-times"></i>
                  Cancel
                </button>
                <button class="reset-btn danger confirm-reset-btn">
                  <i class="fas fa-trash-alt"></i>
                  Reset Database
                </button>
              </div>
            </div>
          `;
          document.body.appendChild(modal);
        }

        hideResetConfirmation() {
          const modal = document.querySelector('.reset-confirmation-modal');
          if (modal) {
            modal.remove();
          }
        }

        async performManualReset() {
          if (this.isLoading) return;

          try {
            this.isLoading = true;
            this.hideResetConfirmation();
            this.showResetStatus('Resetting meal database...', 'info');

            const response = await fetch('/api/dashboard/reset-meals', {
              method: 'POST',
              headers: {
                'Content-Type': 'application/json',
              },
              credentials: 'include'
            });

            const data = await response.json();

            if (data.success) {
              this.showResetStatus(
                `✅ ${data.message} Reset completed at ${data.resetTime}`, 
                'success'
              );
            } else {
              this.showResetStatus(`❌ Reset failed: ${data.error}`, 'error');
            }
          } catch (error) {
            console.error('Reset error:', error);
            this.showResetStatus('❌ Network error. Please check if the backend server is running.', 'error');
          } finally {
            this.isLoading = false;
          }
        }

        async performTestReset() {
          if (this.isLoading) return;

          try {
            this.isLoading = true;
            this.showResetStatus('Running test reset...', 'info');

            setTimeout(() => {
              this.showResetStatus('✅ Test reset completed successfully. No actual data was modified.', 'success');
              this.isLoading = false;
            }, 2000);

          } catch (error) {
            console.error('Test reset error:', error);
            this.showResetStatus('❌ Test reset failed.', 'error');
            this.isLoading = false;
          }
        }

        showResetStatus(message, type) {
          const existingStatus = document.querySelector('.reset-status');
          if (existingStatus) {
            existingStatus.remove();
          }

          const status = document.createElement('div');
          status.className = `reset-status ${type}`;
          status.textContent = message;

          const resetControls = document.querySelector('.reset-controls');
          if (resetControls) {
            resetControls.parentNode.insertBefore(status, resetControls.nextSibling);
          }

          if (type !== 'info') {
            setTimeout(() => {
              if (status.parentNode) {
                status.remove();
              }
            }, 5000);
          }
        }

        updateScheduleDisplay() {
          const scheduleContainer = document.querySelector('.schedule-list');
          if (!scheduleContainer) return;

          const scheduleItems = [
            { time: this.resetSchedule.afterLateNight, meal: 'After Late Night Meal' },
            { time: this.resetSchedule.afterBreakfast, meal: 'After Breakfast' },
            { time: this.resetSchedule.afterLunch, meal: 'After Lunch' },
            { time: this.resetSchedule.afterDinner, meal: 'After Dinner' }
          ];

          scheduleContainer.innerHTML = scheduleItems.map(item => `
            <li>
              <span class="schedule-time">${item.time} EAT</span>
              <span class="schedule-meal">${item.meal}</span>
            </li>
          `).join('');
        }
      }

      window.databaseResetManager = new DatabaseResetManager();
      console.log('DatabaseResetManager created inline');

      // Initialize when database reset section is active
      const checkAndInit = () => {
        const resetSection = document.querySelector('.database-reset-section');
        if (resetSection && !resetSection.dataset.initialized) {
          console.log('Database reset section found, initializing');
          resetSection.dataset.initialized = 'true';
          window.databaseResetManager.init();
        }
      };

      checkAndInit();

      // Also observe for when section becomes active
      const observer = new MutationObserver(() => {
        checkAndInit();
      });

      observer.observe(document.body, { childList: true, subtree: true });
    };

    initDatabaseReset();

    return () => {
      // Cleanup script on unmount
      const existingScript = document.querySelector('script[src="/src/components/DatabaseReset.js"]');
      if (existingScript) {
        document.head.removeChild(existingScript);
      }
    };
  }, []);

  // Focus search input when switching to students section
  useEffect(() => {
    if (activeSection === 'students' && searchInputRef.current) {
      // Small delay to ensure the input is rendered
      setTimeout(() => {
        searchInputRef.current.focus();
      }, 100);
    }
  }, [activeSection]);

  // OTP countdown timer
  useEffect(() => {
    let interval;
    if (showOTPModal && otpCountdown > 0) {
      interval = setInterval(() => {
        setOtpCountdown(prev => {
          if (prev <= 1) {
            setOtpMessage('Verification code expired. Please request a new one.');
            return 0;
          }
          return prev - 1;
        });
      }, 1000);
    }
    return () => clearInterval(interval);
  }, [showOTPModal, otpCountdown]);

  // Resend OTP countdown
  useEffect(() => {
    let interval;
    if (resendCountdown > 0) {
      interval = setInterval(() => {
        setResendCountdown(prev => {
          if (prev <= 1) {
            setCanResendOTP(true);
            return 0;
          }
          return prev - 1;
        });
      }, 1000);
    }
    return () => clearInterval(interval);
  }, [resendCountdown]);

  const fetchDashboardData = async () => {
    try {
      setIsLoading(true);
      setRefreshMessage('');
      console.log('Fetching dashboard data...');
      const response = await fetch('/api/dashboard/stats');
      console.log('Response status:', response.status);

      const data = await response.json();
      console.log('Response data:', data);

      if (response.ok && data.success) {
        setStats({
          totalStudents: data.stats.totalStudents || 0,
          todayAttendance: data.stats.todayAttendance || 0,
          weeklyAttendance: data.stats.weeklyAttendance || 0,
          attendancePercentage: data.stats.attendancePercentage || 0
        });
        setLowAttendanceAlert(data.lowAttendanceAlert);
        setAttendanceData(data.recentAttendance || []);
        setIsLoading(false);

        // Update last refreshed time
        const now = new Date();
        const timeString = now.toLocaleTimeString('en-US', {
          hour: '2-digit',
          minute: '2-digit',
          second: '2-digit',
          hour12: true
        });
        setLastUpdated(timeString);
        setRefreshMessage('Data has been refreshed successfully!');

        // Clear success message after 3 seconds
        setTimeout(() => {
          setRefreshMessage('');
        }, 3000);
      } else {
        console.error('API error:', data.error || 'Unknown error');
        setStats({
          totalStudents: 0,
          todayAttendance: 0,
          weeklyAttendance: 0
        });
        setAttendanceData([]);
        setIsLoading(false);
        setRefreshMessage('Failed to refresh data. Please try again.');

        setTimeout(() => {
          setRefreshMessage('');
        }, 3000);
      }
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
      setIsLoading(false);
      setRefreshMessage('Failed to refresh data. Please try again.');

      // Clear error message after 3 seconds
    }
  };

  const handleStudentSearch = async (query) => {
    if (!query || query.trim() === '') {
      setSearchResults([]);
      setSearchMessage('');
      return;
    }

    try {
      setSearchLoading(true);
      setSearchMessage('');

      console.log('Searching for:', query);
      const response = await fetch(`/api/dashboard/search?query=${encodeURIComponent(query.trim())}`);
      console.log('Search response status:', response.status);

      const data = await response.json();
      console.log('Search response data:', data);

      if (response.ok && data.success) {
        setSearchResults(data.students);
        if (data.students.length === 0) {
          setSearchMessage('No students found matching your search.');
        } else {
