      lunch: {
        startTime: '12:00',
        endTime: '14:00',
        beforeWindow: 30,
        afterWindow: 30,
        enabled: true
      },
      dinner: {
        startTime: '18:00',
        endTime: '20:00',
        beforeWindow: 30,
        afterWindow: 30,
        enabled: true
      },
      lateNight: {
        startTime: '22:00',
        endTime: '23:30',
        beforeWindow: 15,
        afterWindow: 15,
        enabled: false
      }
    });
    setMealWindowsMessage('✅ Meal windows reset to defaults!');
    setTimeout(() => {
      setMealWindowsMessage('');
    }, 3000);
