      } else {
        setSettingsMessage('❌ ' + (data.error || 'Failed to save settings.'));
      }
    } catch (error) {
      console.error('Fetch error:', error);
      setSettingsMessage('❌ Network error: ' + error.message);
    } finally {
      setSettingsLoading(false);
