      } else {
        setAddStaffMessage(data.error || 'Failed to register staff.');
      }
    } catch (error) {
      console.error('Error adding staff:', error);
      setAddStaffMessage('Network error. Please check if the backend server is running.');
    } finally {
      setAddStaffLoading(false);
