import React, { useState, useEffect } from 'react';
import './Dashboard.css';

const Dashboard = ({ user, onLogout }) => {
  const [attendanceData, setAttendanceData] = useState([]);
  const [stats, setStats] = useState({
    totalStudents: 0,
    todayAttendance: 0,
    weeklyAttendance: 0
  });
  const [isLoading, setIsLoading] = useState(true);

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      const response = await fetch('/api/dashboard/stats');
      const data = await response.json();

      if (response.ok) {
        setStats(data.stats);
        setAttendanceData(data.recentAttendance || []);
      }
    } catch (error) {
      console.error('Error fetching dashboard data:', error);
    } finally {
      setIsLoading(false);
    }
  };

  const handleExportData = async () => {
    try {
      const response = await fetch('/api/dashboard/export');
      const blob = await response.blob();
      const url = window.URL.createObjectURL(blob);
      const a = document.createElement('a');
      a.href = url;
      a.download = `attendance_report_${new Date().toISOString().split('T')[0]}.csv`;
      document.body.appendChild(a);
      a.click();
      window.URL.revokeObjectURL(url);
      document.body.removeChild(a);
    } catch (error) {
      console.error('Error exporting data:', error);
    }
  };

  if (isLoading) {
    return (
      <div className="dashboard-container">
        <div className="loading">Loading dashboard...</div>
      </div>
    );
  }

  return (
    <div className="dashboard-container">
      <div className="dashboard-header">
        <div className="header-left">
          <img src="/images/salale_university_logo.png" alt="Salale University" />
          <h1>Admin Dashboard</h1>
        </div>
        <div className="header-right">
          <span className="welcome-text">Welcome, {user?.username}</span>
          <button onClick={onLogout} className="logout-btn">
            <i className="fas fa-sign-out-alt"></i> Logout
          </button>
        </div>
      </div>

      <div className="dashboard-content">
        <div className="stats-grid">
          <div className="stat-card">
            <div className="stat-icon">
              <i className="fas fa-users"></i>
            </div>
            <div className="stat-info">
              <h3>{stats.totalStudents}</h3>
              <p>Total Students</p>
            </div>
          </div>

          <div className="stat-card">
            <div className="stat-icon">
              <i className="fas fa-calendar-day"></i>
            </div>
            <div className="stat-info">
              <h3>{stats.todayAttendance}</h3>
              <p>Today's Attendance</p>
            </div>
          </div>

          <div className="stat-card">
            <div className="stat-icon">
              <i className="fas fa-chart-line"></i>
            </div>
            <div className="stat-info">
              <h3>{stats.weeklyAttendance}</h3>
              <p>This Week</p>
            </div>
          </div>
        </div>

        <div className="dashboard-actions">
          <button onClick={handleExportData} className="export-btn">
            <i className="fas fa-download"></i> Export Attendance Data
          </button>
          <button onClick={fetchDashboardData} className="refresh-btn">
            <i className="fas fa-sync-alt"></i> Refresh Data
          </button>
        </div>

        <div className="recent-attendance">
          <h2>Recent Attendance Records</h2>
          <div className="attendance-table">
            <table>
              <thead>
                <tr>
                  <th>Student ID</th>
                  <th>Name</th>
                  <th>Date</th>
                  <th>Time</th>
                  <th>Meal Type</th>
                  <th>Status</th>
                </tr>
              </thead>
              <tbody>
                {attendanceData.length > 0 ? (
                  attendanceData.map((record, index) => (
                    <tr key={index}>
                      <td>{record.studentId}</td>
                      <td>{record.studentName}</td>
                      <td>{new Date(record.date).toLocaleDateString()}</td>
                      <td>{new Date(record.date).toLocaleTimeString()}</td>
                      <td>
                        <span className={`meal-type ${record.mealType?.toLowerCase()}`}>
                          {record.mealType || 'lunch'}
                        </span>
                      </td>
                      <td>
                        <span className={`status ${record.status?.toLowerCase()}`}>
                          {record.status || 'Present'}
                        </span>
                      </td>
                    </tr>
                  ))
                ) : (
                  <tr>
                    <td colSpan="6" className="no-data">No recent attendance records</td>
                  </tr>
                )}
              </tbody>
            </table>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
